import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

const Admin = () => {
  const [bills, setBills] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    const fetchBills = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/bills', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        
        const data = await response.json();
        
        if (response.ok) {
          setBills(data);
        } else {
          alert(data.message || 'বিল ডেটা লোড করতে সমস্যা হয়েছে');
        }
      } catch (error) {
        alert('বিল ডেটা লোড করতে সমস্যা হয়েছে');
        console.error('Bills fetch error:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && user.role === 'admin') {
      fetchBills();
    }
  }, [user]);

  const handleStatusChange = async (billId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/bills/${billId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ status: newStatus })
      });
      
      const data = await response.json();
      
      if (response.ok) {
        setBills(prev => prev.map(bill => 
          bill._id === billId ? { ...bill, status: newStatus } : bill
        ));
        alert('বিল স্ট্যাটাস আপডেট করা হয়েছে');
      } else {
        alert(data.message || 'স্ট্যাটাস আপডেট করতে সমস্যা হয়েছে');
      }
    } catch (error) {
      alert('স্ট্যাটাস আপডেট করতে সমস্যা হয়েছে');
      console.error('Status update error:', error);
    }
  };

  if (loading) return <div className="container">লোড হচ্ছে...</div>;

  return (
    <div className="container">
      <h1>অ্যাডমিন প্যানেল</h1>
      <div className="card">
        <h3>বিল ম্যানেজমেন্ট</h3>
        {bills.length === 0 ? (
          <p>কোন বিল পাওয়া যায়নি</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>বিল ID</th>
                <th>গ্রাহক</th>
                <th>ইমেইল</th>
                <th>ফোন</th>
                <th>মোট Amount</th>
                <th>জমাদানকারী</th>
                <th>তারিখ</th>
                <th>স্ট্যাটাস</th>
                <th>অ্যাকশন</th>
              </tr>
            </thead>
            <tbody>
              {bills.map(bill => (
                <tr key={bill._id}>
                  <td>{bill._id.substring(0, 8)}...</td>
                  <td>{bill.customerName}</td>
                  <td>{bill.customerEmail}</td>
                  <td>{bill.customerPhone}</td>
                  <td>{bill.grandTotal} টাকা</td>
                  <td>{bill.submittedBy?.name}</td>
                  <td>{new Date(bill.submissionDate).toLocaleDateString()}</td>
                  <td>{bill.status}</td>
                  <td>
                    <select 
                      value={bill.status} 
                      onChange={(e) => handleStatusChange(bill._id, e.target.value)}
                    >
                      <option value="Pending">Pending</option>
                      <option value="Approved">Approved</option>
                      <option value="Rejected">Rejected</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Admin;
