import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';

const DataTable = ({ items: initialItems, grandTotal: initialGrandTotal }) => {
  const [items, setItems] = useState(initialItems || []);
  const [grandTotal, setGrandTotal] = useState(initialGrandTotal || 0);
  const [customerName, setCustomerName] = useState('');
  const [customerEmail, setCustomerEmail] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const { user } = useAuth();

  const handleDiscountChange = (index, discount) => {
    const newItems = [...items];
    newItems[index].discount = Math.max(0, Math.min(100, discount));
    newItems[index].total = newItems[index].price * newItems[index].quantity * (1 - newItems[index].discount / 100);
    
    setItems(newItems);
    setGrandTotal(newItems.reduce((sum, item) => sum + item.total, 0));
  };

  const handleSubmit = async () => {
    if (!customerName || !customerEmail || !customerPhone) {
      alert('দয়া করে গ্রাহকের তথ্য প্রদান করুন');
      return;
    }

    setSubmitting(true);
    
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/submit-bill', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          customerName,
          customerEmail,
          customerPhone,
          items,
          grandTotal
        })
      });
      
      const data = await response.json();
      
      if (response.ok) {
        alert('বিল সফলভাবে জমা দেওয়া হয়েছে!');
        setItems([]);
        setGrandTotal(0);
        setCustomerName('');
        setCustomerEmail('');
        setCustomerPhone('');
      } else {
        alert(data.message || 'বিল জমা দেওয়া ব্যর্থ হয়েছে');
      }
    } catch (error) {
      alert('বিল জমা দেওয়া ব্যর্থ হয়েছে');
      console.error('Submission error:', error);
    } finally {
      setSubmitting(false);
    }
  };

  if (!items || items.length === 0) {
    return (
      <div className="card">
        <h3>বিল ডেটা</h3>
        <p>কোন ডেটা পাওয়া যায়নি। দয়া করে একটি এক্সেল ফাইল আপলোড করুন।</p>
      </div>
    );
  }

  return (
    <div className="card">
      <h3>বিল ডেটা</h3>
      
      <div style={{ marginBottom: '20px' }}>
        <h4>গ্রাহকের তথ্য</h4>
        <div className="form-group">
          <label>গ্রাহকের নাম:</label>
          <input
            type="text"
            value={customerName}
            onChange={(e) => setCustomerName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>ইমেইল:</label>
          <input
            type="email"
            value={customerEmail}
            onChange={(e) => setCustomerEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>ফোন নম্বর:</label>
          <input
            type="tel"
            value={customerPhone}
            onChange={(e) => setCustomerPhone(e.target.value)}
            required
          />
        </div>
      </div>
      
      <table>
        <thead>
          <tr>
            <th>প্রডাক্ট</th>
            <th>মূল্য</th>
            <th>পরিমাণ</th>
            <th>ডিসকাউন্ট (%)</th>
            <th>মোট</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>{item.product}</td>
              <td>{item.price} টাকা</td>
              <td>{item.quantity}</td>
              <td>
                <input
                  type="number"
                  min="0"
                  max="100"
                  value={item.discount}
                  onChange={(e) => handleDiscountChange(index, parseInt(e.target.value))}
                  style={{ width: '60px' }}
                />
              </td>
              <td>{item.total.toFixed(2)} টাকা</td>
            </tr>
          ))}
        </tbody>
        <tfoot>
          <tr>
            <td colSpan="4" style={{ textAlign: 'right', fontWeight: 'bold' }}>মোট:</td>
            <td style={{ fontWeight: 'bold' }}>{grandTotal.toFixed(2)} টাকা</td>
          </tr>
        </tfoot>
      </table>
      
      <div style={{ marginTop: '20px' }}>
        <button 
          className="btn btn-success" 
          onClick={handleSubmit}
          disabled={submitting || !user}
        >
          {submitting ? 'জমা দেওয়া হচ্ছে...' : 'বিল সাবমিট করুন'}
        </button>
        {!user && (
          <p style={{ color: 'red', marginTop: '10px' }}>বিল জমা দিতে লগইন প্রয়োজন</p>
        )}
      </div>
    </div>
  );
};

export default DataTable;
