// client/src/pages/ClientList.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

const ClientList = () => {
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user } = useAuth();

  useEffect(() => {
    const fetchClients = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/clients', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        
        const data = await response.json();
        
        if (response.ok) {
          setClients(data.clients || []);
        } else {
          setError(data.message || 'ক্লায়েন্ট লোড করতে সমস্যা হয়েছে');
        }
      } catch (error) {
        setError('নেটওয়ার্ক সমস্যা হয়েছে। সার্ভার চেক করুন।');
        console.error('Clients fetch error:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchClients();
    }
  }, [user]);

  if (loading) return <div className="container">লোড হচ্ছে...</div>;
  if (error) return (
    <div className="container">
      <div style={{ color: 'red', marginBottom: '20px' }}>{error}</div>
      <button onClick={() => window.location.reload()} style={{ padding: '10px 20px' }}>
        আবার চেষ্টা করুন
      </button>
    </div>
  );

  return (
    <div className="container">
      <h1>রেজিস্টার্ড ক্লায়েন্ট তালিকা</h1>
      
      {clients.length === 0 ? (
        <div className="card">
          <p>কোন রেজিস্টার্ড ক্লায়েন্ট পাওয়া যায়নি</p>
        </div>
      ) : (
        <div className="card">
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ backgroundColor: '#f8f9fa' }}>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>নাম</th>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>ঠিকানা</th>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>ইমেইল</th>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>ফোন</th>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>রেজিস্ট্রেশন তারিখ</th>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>রেজিস্টার করেছেন</th>
                </tr>
              </thead>
              <tbody>
                {clients.map(client => (
                  <tr key={client._id}>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{client.name}</td>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{client.address}</td>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{client.email}</td>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{client.phone}</td>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                      {new Date(client.registrationDate).toLocaleDateString()}
                    </td>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                      {client.registeredBy?.name || 'Unknown'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default ClientList;
