import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';

const FileUpload = ({ onDataProcessed }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const { user } = useAuth();

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('দয়া করে একটি ফাইল নির্বাচন করুন');
      return;
    }

    if (!user) {
      alert('দয়া করে প্রথমে লগইন করুন');
      return;
    }

    setUploading(true);
    
    const formData = new FormData();
    formData.append('excelFile', selectedFile);
    
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });
      
      const data = await response.json();
      
      if (response.ok) {
        onDataProcessed(data.items, data.grandTotal);
        alert('ফাইল সফলভাবে আপলোড হয়েছে!');
      } else {
        alert(data.message || 'ফাইল আপলোড ব্যর্থ হয়েছে');
      }
    } catch (error) {
      alert('ফাইল আপলোড ব্যর্থ হয়েছে');
      console.error('Upload error:', error);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="card">
      <h3>এক্সেল ফাইল আপলোড</h3>
      <div className="form-group">
        <input 
          type="file" 
          accept=".xlsx, .xls, .csv" 
          onChange={handleFileChange}
          disabled={!user}
        />
      </div>
      {selectedFile && (
        <p>নির্বাচিত ফাইল: {selectedFile.name}</p>
      )}
      <button 
        className="btn btn-primary" 
        onClick={handleUpload}
        disabled={!selectedFile || uploading || !user}
      >
        {uploading ? 'আপলোড হচ্ছে...' : 'ফাইল আপলোড করুন'}
      </button>
      {!user && (
        <p style={{ color: 'red', marginTop: '10px' }}>ফাইল আপলোড করতে লগইন প্রয়োজন</p>
      )}
    </div>
  );
};

export default FileUpload;
