import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import { useAuth } from "../context/AuthContext";

const FileUpload = ({ onDataProcessed }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const { user } = useAuth();
  const [processedData, setProcessedData] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const savedData = localStorage.getItem("processedExcelData");
    if (savedData) {
      try {
        const parsedData = JSON.parse(savedData);
        setProcessedData(parsedData);
        if (onDataProcessed) {
          onDataProcessed(parsedData.items, parsedData.grandTotal, parsedData.totalDiscount || 0);
        }
      } catch (error) {
        console.error("Error parsing saved data:", error);
        localStorage.removeItem("processedExcelData");
        setError("‡¶∏‡¶Ç‡¶∞‡¶ï‡ßç‡¶∑‡¶ø‡¶§ ‡¶°‡ßá‡¶ü‡¶æ ‡¶≤‡ßã‡¶° ‡¶ï‡¶∞‡¶§‡ßá ‡¶∏‡¶Æ‡¶∏‡ßç‡¶Ø‡¶æ ‡¶π‡ßü‡ßá‡¶õ‡ßá");
      }
    }
  }, [onDataProcessed]);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setError("");
  };

  const fetchClientRate = async (name, address) => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://localhost:5000/api/clients/check", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: token ? `Bearer ${token}` : "",
        },
        body: JSON.stringify({ name, address }),
      });

      if (!res.ok) {
        throw new Error(`Server returned ${res.status} ${res.statusText}`);
      }

      const data = await res.json();
      if (data.success && data.client) {
        return {
          ratePerKg: data.client.ratePerKg ?? 0,
          usdSurcharge: data.client.usdSurcharge ?? 0,
          baseRate: data.client.baseRate ?? 0,
          extraRatePerKg: data.client.extraRatePerKg ?? 0,
          discountType: data.client.discountType ?? 'percentage',
          discountValue: data.client.discountValue ?? 0,
        };
      }
    } catch (err) {
      console.error("Rate fetch error:", err);
      setError(`‡¶ï‡ßç‡¶≤‡¶æ‡¶á‡¶®‡ßç‡¶ü ‡¶∞‡ßá‡¶ü ‡¶®‡¶ø‡ßü‡ßá‡¶æ‡¶ó ‡¶ï‡¶∞‡¶§‡ßá ‡¶∏‡¶Æ‡¶∏‡ßç‡¶Ø‡¶æ: ${err.message}`);
    }
    return null;
  };

  const applyDiscount = (price, discountType, discountValue) => {
    if (!discountValue || discountValue <= 0) return price;

    if (discountType === 'percentage') {
      return price - (price * discountValue / 100);
    } else if (discountType === 'fixed') {
      return Math.max(0, price - discountValue);
    }

    return price;
  };

  const processExcelFile = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();

      reader.onload = async (e) => {
        try {
          const data = new Uint8Array(e.target.result);
          const workbook = XLSX.read(data, { type: "array" });
          const sheetName = workbook.SheetNames[0];
          const worksheet = workbook.Sheets[sheetName];

          const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });
          const items = [];

          if (!jsonData || jsonData.length === 0) {
            throw new Error("Excel ‡¶´‡¶æ‡¶á‡¶≤ ‡¶ñ‡¶æ‡¶≤‡¶ø ‡¶¨‡¶æ ‡¶´‡¶∞‡¶Æ‡ßç‡¶Ø‡¶æ‡¶ü ‡¶∏‡¶†‡¶ø‡¶ï ‡¶®‡ßü‡•§");
          }

          let startRow = 0;
          for (let i = 0; i < jsonData.length; i++) {
            const row = jsonData[i];
            if (!row) continue;
            if (
              row.includes("NO") ||
              row.includes("AWB NO") ||
              row.includes("SHIPPER")
            ) {
              startRow = i;
              break;
            }
          }

          if (startRow === 0 && jsonData.length > 0) {
            startRow = -1;
          }

          for (let row = startRow + 1; row < jsonData.length; row++) {
            const rowData = jsonData[row];
            if (!rowData || rowData.length === 0) continue;

            const no = rowData[0] || "";
            const awbNo = rowData[1] || "";
            const shipper = rowData[2] || "";
            const shipperAddress = rowData[3] || "";
            const consignee = rowData[4] || "";
            const dest = rowData[5] || "";
            const cneeAddress = rowData[6] || "";
            const nop = rowData[7] || "";
            const wt = parseFloat(rowData[8]) || 0;
            const dsct = rowData[9] || "";
            const cod = rowData[10] || "";
            const binVat = rowData[11] || "";

            let finalPrice = 0;
            let discountAmount = 0;

            if (consignee && wt > 0) {
              const rateConfig = await fetchClientRate(
                consignee.toString().trim(),
                cneeAddress?.toString().trim()
              );

              if (rateConfig) {
                console.log(`Client: ${consignee}, WT: ${wt}, Base Rate: ${rateConfig.baseRate}, Extra Rate: ${rateConfig.extraRatePerKg}, Discount: ${rateConfig.discountValue}${rateConfig.discountType === 'percentage' ? '%' : ''}`);

                // FIXED: Prioritize tiered pricing if baseRate is configured
                if (rateConfig.baseRate > 0) {
                  if (wt <= 1) {
                    finalPrice = rateConfig.baseRate;
                  } else {
                    // Use extraRatePerKg if available, otherwise fall back to baseRate
                    const extraRate = rateConfig.extraRatePerKg > 0 ? rateConfig.extraRatePerKg : rateConfig.baseRate;
                    finalPrice = rateConfig.baseRate + (wt - 1) * extraRate;
                  }
                  console.log(`Using tiered pricing: ${finalPrice} = ${rateConfig.baseRate} + (${wt} - 1) * ${rateConfig.extraRatePerKg > 0 ? rateConfig.extraRatePerKg : rateConfig.baseRate}`);
                }
                // Fallback to old logic
                else if (rateConfig.ratePerKg > 0 && wt === rateConfig.ratePerKg) {
                  finalPrice = rateConfig.usdSurcharge;
                  console.log(`Using USD Surcharge: ${finalPrice} (WT matches RatePerKG)`);
                } 
                else if (rateConfig.ratePerKg > 0) {
                  finalPrice = wt * rateConfig.ratePerKg;
                  console.log(`Using normal calculation: ${finalPrice} = ${wt} √ó ${rateConfig.ratePerKg}`);
                }
                else if (rateConfig.usdSurcharge > 0) {
                  finalPrice = rateConfig.usdSurcharge;
                  console.log(`Using USD Surcharge: ${finalPrice} (No RatePerKG configured)`);
                }

                // Apply discount
                const priceBeforeDiscount = finalPrice;
                finalPrice = applyDiscount(finalPrice, rateConfig.discountType, rateConfig.discountValue);
                discountAmount = priceBeforeDiscount - finalPrice;

                if (discountAmount > 0) {
                  console.log(`Applied discount: ${discountAmount} (${rateConfig.discountValue}${rateConfig.discountType === 'percentage' ? '%' : ''})`);
                }
              } else {
                console.warn(`‡¶ï‡ßã‡¶®‡ßã ‡¶ï‡ßç‡¶≤‡¶æ‡¶á‡¶®‡ßç‡¶ü ‡¶™‡¶æ‡¶ì‡ßü‡¶æ ‡¶Ø‡¶æ‡ßü‡¶®‡¶ø: ${consignee}`);
                finalPrice = wt * 100;
              }
            }

            items.push({
              id: no || row + 1,
              awbNo: awbNo.toString(),
              shipper: shipper.toString().trim(),
              shipperAddress: shipperAddress.toString().trim(),
              dest: dest.toString().trim(),
              customerName: consignee.toString().trim(),
              cneeAddress: cneeAddress.toString().trim(),
              nop: nop.toString(),
              wt: wt.toString(),
              product: dsct.toString().trim(),
              cod: cod.toString(),
              binVat: binVat.toString(),
              price: finalPrice,
              quantity: 1,
              discount: discountAmount,
              total: finalPrice,
            });
          }

          if (items.length === 0) {
            throw new Error("‡¶´‡¶æ‡¶á‡¶≤‡ßá ‡¶ï‡ßã‡¶®‡ßã valid ‡¶°‡ßá‡¶ü‡¶æ ‡¶™‡¶æ‡¶ì‡¶Ø‡¶º‡¶æ ‡¶Ø‡¶æ‡¶Ø‡¶º‡¶®‡¶ø‡•§");
          }

          const grandTotal = items.reduce((sum, item) => sum + (item.total || 0), 0);
          const totalDiscount = items.reduce((sum, item) => sum + (item.discount || 0), 0);
          resolve({ items, grandTotal, totalDiscount });
        } catch (error) {
          reject(error);
        }
      };

      reader.onerror = (error) => {
        reject(new Error("‡¶´‡¶æ‡¶á‡¶≤ ‡¶™‡¶°‡¶º‡¶§‡ßá ‡¶∏‡¶Æ‡¶∏‡ßç‡¶Ø‡¶æ ‡¶π‡ßü‡ßá‡¶õ‡ßá: " + error.message));
      };
      reader.readAsArrayBuffer(file);
    });
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError("‡¶¶‡ßü‡¶æ ‡¶ï‡¶∞‡ßá ‡¶è‡¶ï‡¶ü‡¶ø ‡¶´‡¶æ‡¶á‡¶≤ ‡¶®‡¶ø‡¶∞‡ßç‡¶¨‡¶æ‡¶ö‡¶® ‡¶ï‡¶∞‡ßÅ‡¶®");
      return;
    }

    setUploading(true);
    setError("");

    try {
      const result = await processExcelFile(selectedFile);

      if (result.items.length === 0) {
        setError("‡¶ï‡ßã‡¶®‡ßã valid ‡¶°‡ßá‡¶ü‡¶æ ‡¶™‡¶æ‡¶ì‡ßü‡¶æ ‡¶Ø‡¶æ‡ßü‡¶®‡¶ø‡•§");
        return;
      }

      setProcessedData(result);
      localStorage.setItem("processedExcelData", JSON.stringify(result));

      onDataProcessed(result.items, result.grandTotal, result.totalDiscount || 0);
      alert(
        `‡¶´‡¶æ‡¶á‡¶≤ ‡¶∏‡¶´‡¶≤‡¶≠‡¶æ‡¶¨‡ßá ‡¶™‡ßç‡¶∞‡¶∏‡ßá‡¶∏ ‡¶π‡ßü‡ßá‡¶õ‡ßá! ${result.items.length} ‡¶ü‡¶ø ‡¶Ü‡¶á‡¶ü‡ßá‡¶Æ ‡¶™‡¶æ‡¶ì‡ßü‡¶æ ‡¶ó‡ßá‡¶õ‡ßá‡•§ ‡¶Æ‡ßã‡¶ü ‡¶°‡¶ø‡¶∏‡¶ï‡¶æ‡¶â‡¶®‡ßç‡¶ü: ${(result.totalDiscount || 0).toFixed(2)}`
      );
    } catch (error) {
      console.error("File processing error:", error);
      setError("‡¶´‡¶æ‡¶á‡¶≤ ‡¶™‡ßç‡¶∞‡¶∏‡ßá‡¶∏‡¶ø‡¶Ç ‡¶¨‡ßç‡¶Ø‡¶∞‡ßç‡¶•: " + error.message);
    } finally {
      setUploading(false);
    }
  };

  const clearData = () => {
    setProcessedData(null);
    setSelectedFile(null);
    setError("");
    localStorage.removeItem("processedExcelData");
    if (onDataProcessed) {
      onDataProcessed([], 0, 0);
    }
    alert("‡¶°‡ßá‡¶ü‡¶æ ‡¶∏‡¶æ‡¶´ ‡¶ï‡¶∞‡¶æ ‡¶π‡¶Ø‡¶º‡ßá‡¶õ‡ßá‡•§");
  };

  return (
    <div className="card">
      <h3>‡¶è‡¶ï‡ßç‡¶∏‡ßá‡¶≤ ‡¶´‡¶æ‡¶á‡¶≤ ‡¶Ü‡¶™‡¶≤‡ßã‡¶°</h3>

      {error && (
        <div className="alert alert-error" style={{color: "#721c24", backgroundColor: "#f8d7da", borderColor: "#f5c6cb", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}>
          {error}
        </div>
      )}

      {processedData && (
        <div
          style={{
            marginBottom: "20px",
            padding: "10px",
            backgroundColor: "#e7f4e4",
            borderRadius: "5px",
          }}
        >
          <h4>‚úÖ ‡¶™‡ßç‡¶∞‡¶∏‡ßá‡¶∏ ‡¶ï‡¶∞‡¶æ ‡¶°‡ßá‡¶ü‡¶æ ‡¶∏‡¶Ç‡¶∞‡¶ï‡ßç‡¶∑‡¶ø‡¶§ ‡¶Ü‡¶õ‡ßá</h4>
          <p>‡¶Æ‡ßã‡¶ü ‡¶Ü‡¶á‡¶ü‡ßá‡¶Æ: {processedData.items.length}‡¶ü‡¶ø</p>
          <p>‡¶Æ‡ßã‡¶ü ‡¶Æ‡ßÇ‡¶≤‡ßç‡¶Ø: {(processedData.grandTotal || 0).toFixed(2)}</p>
          <p>‡¶Æ‡ßã‡¶ü ‡¶°‡¶ø‡¶∏‡¶ï‡¶æ‡¶â‡¶®‡ßç‡¶ü: {(processedData.totalDiscount || 0).toFixed(2)}</p>
          <button
            className="btn btn-secondary"
            onClick={clearData}
            style={{ marginTop: "10px" }}
          >
            ‡¶°‡ßá‡¶ü‡¶æ ‡¶∏‡¶æ‡¶´ ‡¶ï‡¶∞‡ßÅ‡¶®
          </button>
        </div>
      )}

      <div className="form-group">
        <label>‡¶è‡¶ï‡ßç‡¶∏‡ßá‡¶≤ ‡¶´‡¶æ‡¶á‡¶≤ ‡¶®‡¶ø‡¶∞‡ßç‡¶¨‡¶æ‡¶ö‡¶® ‡¶ï‡¶∞‡ßÅ‡¶® (.xlsx, .xls, .csv):</label>
