import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const ClientProfile = () => {
  const [client, setClient] = useState(null);
  const [rateConfigs, setRateConfigs] = useState([]);
  const [newRate, setNewRate] = useState({
    shipperAddressPattern: '',
    weight: '',
    rate: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user } = useAuth();
  const { id } = useParams();

  useEffect(() => {
    const fetchClient = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:5000/api/clients/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        
        const data = await response.json();
        
        if (response.ok) {
          setClient(data.client);
          setRateConfigs(data.client.rateConfigurations || []);
        } else {
          setError(data.message || 'ক্লায়েন্ট লোড করতে সমস্যা হয়েছে');
        }
      } catch (error) {
        setError('ক্লায়েন্ট লোড করতে সমস্যা হয়েছে');
        console.error('Client fetch error:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && id) {
      fetchClient();
    }
  }, [user, id]);

  const handleAddRate = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/clients/${id}/rates`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(newRate)
      });
      
      const data = await response.json();
      
      if (response.ok) {
        setRateConfigs([...rateConfigs, data]);
        setNewRate({ shipperAddressPattern: '', weight: '', rate: '' });
      } else {
        setError(data.message || 'রেট যোগ করতে সমস্যা হয়েছে');
      }
    } catch (error) {
      setError('রেট যোগ করতে সমস্যা হয়েছে');
      console.error('Add rate error:', error);
    }
  };

  if (loading) return <div className="container">লোড হচ্ছে...</div>;
  if (error) return <div className="container" style={{ color: 'red' }}>{error}</div>;
  if (!client) return <div className="container">ক্লায়েন্ট পাওয়া যায়নি</div>;

  return (
    <div className="container">
      <Link to="/clients" style={{ marginBottom: '20px', display: 'block' }}>
        &larr; ক্লায়েন্ট তালিকায় ফিরে যান
      </Link>
      
      <div className="card">
        <h2>{client.name} - প্রোফাইল</h2>
        <p><strong>ঠিকানা:</strong> {client.address}</p>
        <p><strong>ইমেইল:</strong> {client.email}</p>
        <p><strong>ফোন:</strong> {client.phone}</p>
        <p><strong>রেজিস্ট্রেশন তারিখ:</strong> {new Date(client.registrationDate).toLocaleDateString()}</p>
      </div>

      <div className="card" style={{ marginTop: '20px' }}>
        <h3>রেট কনফিগারেশন</h3>
        
        <div style={{ marginBottom: '20px' }}>
          <div style={{ display: 'flex', gap: '10px', marginBottom: '10px', flexWrap: 'wrap' }}>
            <input
              placeholder="শিপার অ্যাড্রেস প্যাটার্ন"
              value={newRate.shipperAddressPattern}
              onChange={(e) => setNewRate({...newRate, shipperAddressPattern: e.target.value})}
              style={{ padding: '8px', flex: '1', minWidth: '150px' }}
            />
            <input
              type="number"
              placeholder="ওজন (kg)"
              value={newRate.weight}
              onChange={(e) => setNewRate({...newRate, weight: e.target.value})}
              style={{ padding: '8px', width: '120px' }}
            />
            <input
              type="number"
              placeholder="রেট"
              value={newRate.rate}
              onChange={(e) => setNewRate({...newRate, rate: e.target.value})}
              style={{ padding: '8px', width: '120px' }}
            />
            <button 
              onClick={handleAddRate}
              style={{ padding: '8px 16px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px' }}
            >
              রেট যোগ করুন
            </button>
          </div>
        </div>

        {rateConfigs.length === 0 ? (
          <p>কোন রেট কনফিগারেশন নেই</p>
        ) : (
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ backgroundColor: '#f8f9fa' }}>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>শিপার অ্যাড্রেস প্যাটার্ন</th>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>ওজন (kg)</th>
                  <th style={{ padding: '12px', border: '1px solid #dee2e6' }}>রেট</th>
                </tr>
              </thead>
              <tbody>
                {rateConfigs.map((rate, index) => (
                  <tr key={index}>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{rate.shipperAddressPattern}</td>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{rate.weight}</td>
                    <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{rate.rate}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default ClientProfile;
