import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';

const FileUpload = ({ onDataProcessed }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const { user } = useAuth();

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('দয়া করে একটি ফাইল নির্বাচন করুন');
      return;
    }

    if (!user) {
      alert('দয়া করে প্রথমে লগইন করুন');
      return;
    }

    // File validation
    const validExtensions = ['.xlsx', '.xls', '.csv'];
    const fileExtension = selectedFile.name.toLowerCase().substring(selectedFile.name.lastIndexOf('.'));
    
    if (!validExtensions.includes(fileExtension)) {
      alert('দয়া করে শুধুমাত্র .xlsx, .xls, বা .csv ফাইল নির্বাচন করুন');
      return;
    }

    setUploading(true);
    
    const formData = new FormData();
    formData.append('excelFile', selectedFile);
    
    try {
      const token = localStorage.getItem('token');
      console.log('Uploading file:', selectedFile.name); // Debugging
      
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });
      
      const data = await response.json();
      console.log('Server response:', data); // Debugging
      
      if (response.ok) {
        if (data.success) {
          onDataProcessed(data.items, data.grandTotal);
          alert(data.message || 'ফাইল সফলভাবে আপলোড হয়েছে!');
        } else {
          alert(data.message || 'ফাইল প্রসেসিং ব্যর্থ হয়েছে');
        }
      } else {
        alert(data.message || 'ফাইল আপলোড ব্যর্থ হয়েছে');
      }
    } catch (error) {
      console.error('Upload error:', error);
      alert('নেটওয়ার্ক ত্রুটি: ফাইল আপলোড ব্যর্থ হয়েছে');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="card">
      <h3>এক্সেল ফাইল আপলোড</h3>
      <div className="form-group">
        <label>এক্সেল ফাইল নির্বাচন করুন (.xlsx, .xls, .csv):</label>
        <input 
          type="file" 
          accept=".xlsx, .xls, .csv" 
          onChange={handleFileChange}
          disabled={!user}
        />
      </div>
      {selectedFile && (
        <div>
          <p>নির্বাচিত ফাইল: {selectedFile.name}</p>
          <p>ফাইলের সাইজ: {(selectedFile.size / 1024).toFixed(2)} KB</p>
        </div>
      )}
      <button 
        className="btn btn-primary" 
        onClick={handleUpload}
        disabled={!selectedFile || uploading || !user}
      >
        {uploading ? 'আপলোড হচ্ছে...' : 'ফাইল আপলোড করুন'}
      </button>
      {!user && (
        <p style={{ color: 'red', marginTop: '10px' }}>ফাইল আপলোড করতে লগইন প্রয়োজন</p>
      )}
    </div>
  );
};

export default FileUpload;
