import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { useAuth } from '../context/AuthContext';

const FileUpload = ({ onDataProcessed }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const { user } = useAuth();

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

 const processExcelFile = (file) => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    
    reader.onload = (e) => {
      try {
        const data = new Uint8Array(e.target.result);
        const workbook = XLSX.read(data, { type: 'array' });
        const sheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[sheetName];
        
        // প্রথমে শুধু headers দেখুন
        const range = XLSX.utils.decode_range(worksheet['!ref']);
        let headers = [];
        
        for (let C = range.s.c; C <= range.e.c; ++C) {
          const cellAddress = { c: C, r: range.s.r };
          const cellRef = XLSX.utils.encode_cell(cellAddress);
          const cell = worksheet[cellRef];
          headers.push(cell ? cell.v : `Column ${C}`);
        }
        
        console.log('Excel Headers:', headers);

          // আপনার Excel structure অনুযায়ী data process করুন
          const items = jsonData
            .map((item, index) => {
              try {
                // বিভিন্ন possible column names check করুন
                const customerName = item['학년'] || item['নাম'] || item['Name'] || item['Customer Name'] || `Customer ${index + 1}`;
                const phone = item['학년명'] || item['Phone'] || item['ফোন'] || item['Contact'] || '';
                const product = item['학회관리자'] || item['Product'] || item['পণ্য'] || item['Item'] || '';
                
                // Amount extract করুন - বিভিন্ন format handle করুন
                let amount = 0;
                const amountText = item['응답 과제공항'] || item['Amount'] || item['পরিমাণ'] || item['Price'] || '0';
                
                if (typeof amountText === 'string') {
                  // "t.s,000" বা "1,000" format handle করুন
                  const cleanAmount = amountText
                    .replace('t.', '') // "t." remove করুন
                    .replace('s,', '') // "s," remove করুন  
                    .replace(/,/g, '') // commas remove করুন
                    .replace(/\s+/g, '') // spaces remove করুন
                    .replace(/[^\d.]/g, ''); // numbers এবং dot ছাড়া সব remove করুন
                  
                  amount = parseFloat(cleanAmount) || 0;
                } else {
                  amount = parseFloat(amountText) || 0;
                }

                // যদি amount 0 হয় বা invalid হয়, skip করুন
                if (amount <= 0 || !customerName || customerName === '학년') {
                  return null;
                }

                return {
                  id: index + 1,
                  customerName: customerName.trim(),
                  phone: phone.toString().trim(),
                  product: product.trim(),
                  price: amount,
                  quantity: 1,
                  discount: 0,
                  total: amount
                };
              } catch (error) {
                console.error('Error processing row:', error);
                return null;
              }
            })
            .filter(item => item !== null); // null items remove করুন

          console.log('Processed Items:', items); // Debugging জন্য

          if (items.length === 0) {
            throw new Error('ফাইলে কোনো valid ডেটা পাওয়া যায়নি। দয়া করে ফাইল format চেক করুন।');
          }

          const grandTotal = items.reduce((sum, item) => sum + item.total, 0);
          
          resolve({ items, grandTotal });
        } catch (error) {
          reject(error);
        }
      };
      
      reader.onerror = (error) => reject(error);
      reader.readAsArrayBuffer(file);
    });
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('দয়া করে একটি ফাইল নির্বাচন করুন');
      return;
    }

    setUploading(true);
    
    try {
      const result = await processExcelFile(selectedFile);
      
      if (result.items.length === 0) {
        alert('ফাইলে কোনো valid ডেটা পাওয়া যায়নি। দয়া করে ফাইল format চেক করুন।');
        return;
      }
      
      onDataProcessed(result.items, result.grandTotal);
      alert('ফাইল সফলভাবে প্রসেস করা হয়েছে! ' + result.items.length + 'টি আইটেম পাওয়া গেছে।');
    } catch (error) {
      console.error('File processing error:', error);
      alert('ফাইল প্রসেসিং ব্যর্থ হয়েছে: ' + error.message);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="card">
      <h3>এক্সেল ফাইল আপলোড</h3>
      <div className="form-group">
        <label>এক্সেল ফাইল নির্বাচন করুন (.xlsx, .xls, .csv):</label>
        <input 
          type="file" 
          accept=".xlsx, .xls, .csv" 
          onChange={handleFileChange}
        />
      </div>
      {selectedFile && (
        <div>
          <p>নির্বাচিত ফাইল: {selectedFile.name}</p>
          <p>ফাইলের সাইজ: {(selectedFile.size / 1024).toFixed(2)} KB</p>
        </div>
      )}
      <button 
        className="btn btn-primary" 
        onClick={handleUpload}
        disabled={!selectedFile || uploading}
      >
        {uploading ? 'প্রসেস হচ্ছে...' : 'ফাইল প্রসেস করুন'}
      </button>
      
      <div style={{ marginTop: '20px', padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '5px' }}>
        <h4>ফাইল format নির্দেশিকা:</h4>
        <ul>
          <li>কলাম 1: গ্রাহকের নাম (নাম, 학년)</li>
          <li>কলাম 2: ফোন নম্বর (ফোন, 학년명)</li>
          <li>কলাম 3: পণ্যের নাম (পণ্য, 학회관리자)</li>
          <li>কলাম 4: Amount (পরিমাণ, 응답 과제공항)</li>
        </ul>
      </div>
    </div>
  );
};

export default FileUpload;
