import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { useAuth } from '../context/AuthContext';

const FileUpload = ({ onDataProcessed }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const { user } = useAuth();

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const processExcelFile = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      
      reader.onload = (e) => {
        try {
          const data = new Uint8Array(e.target.result);
          const workbook = XLSX.read(data, { type: 'array' });
          const sheetName = workbook.SheetNames[0];
          const worksheet = workbook.Sheets[sheetName];
          const jsonData = XLSX.utils.sheet_to_json(worksheet);
          
          // Process data
          const items = jsonData.map(item => ({
            product: item.Product || item.product || item['Product Name'] || '',
            price: parseFloat(item.Price || item.price || item['Unit Price'] || 0),
            quantity: parseInt(item.Quantity || item.quantity || item['Qty'] || 1),
            discount: parseFloat(item.Discount || item.discount || item['Discount %'] || 0),
            total: 0
          }));

          items.forEach(item => {
            item.total = item.price * item.quantity * (1 - item.discount / 100);
          });

          const grandTotal = items.reduce((sum, item) => sum + item.total, 0);
          
          resolve({ items, grandTotal });
        } catch (error) {
          reject(error);
        }
      };
      
      reader.onerror = (error) => reject(error);
      reader.readAsArrayBuffer(file);
    });
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('দয়া করে একটি ফাইল নির্বাচন করুন');
      return;
    }

    setUploading(true);
    
    try {
      const result = await processExcelFile(selectedFile);
      onDataProcessed(result.items, result.grandTotal);
      alert('ফাইল সফলভাবে প্রসেস করা হয়েছে!');
    } catch (error) {
      console.error('File processing error:', error);
      alert('ফাইল প্রসেসিং ব্যর্থ হয়েছে: ' + error.message);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="card">
      <h3>এক্সেল ফাইল আপলোড</h3>
      <div className="form-group">
        <input 
          type="file" 
          accept=".xlsx, .xls, .csv" 
          onChange={handleFileChange}
        />
      </div>
      {selectedFile && (
        <p>নির্বাচিত ফাইল: {selectedFile.name}</p>
      )}
      <button 
        className="btn btn-primary" 
        onClick={handleUpload}
        disabled={!selectedFile || uploading}
      >
        {uploading ? 'প্রসেস হচ্ছে...' : 'ফাইল প্রসেস করুন'}
      </button>
    </div>
  );
};

export default FileUpload;
