import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import { useAuth } from "../context/AuthContext";

const FileUpload = ({ onDataProcessed }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const { user } = useAuth();
  const [processedData, setProcessedData] = useState(null);

  // ‚úÖ ‡¶´‡¶ø‡¶ï‡ßç‡¶∏‡¶° ‡¶∞‡ßá‡¶ü ‡¶è‡¶ñ‡¶æ‡¶®‡ßá ‡¶∞‡¶æ‡¶ñ‡¶≤‡¶æ‡¶Æ (‡¶π‡¶æ‡¶∞‡ßç‡¶°‡¶ï‡ßã‡¶° ‡¶ï‡¶∞‡¶æ)
  // ‡¶ö‡¶æ‡¶á‡¶≤‡ßá API ‡¶•‡ßá‡¶ï‡ßá ‡¶Ü‡¶®‡¶§‡ßá ‡¶™‡¶æ‡¶∞‡¶¨‡ßá‡¶® (fetchFixedRate ‡¶¨‡ßç‡¶Ø‡¶¨‡¶π‡¶æ‡¶∞ ‡¶ï‡¶∞‡ßá)
  const FIXED_RATE = {
    ratePerKg: 700, // ‡¶™‡ßç‡¶∞‡¶§‡¶ø ‡¶ï‡ßá‡¶ú‡¶ø‡¶∞ ‡¶∞‡ßá‡¶ü
    usdSurcharge: 0, // ‡¶∏‡¶æ‡¶∞‡¶ö‡¶æ‡¶∞‡ßç‡¶ú
  };

  // ‡¶ö‡¶æ‡¶á‡¶≤‡ßá Backend ‡¶•‡ßá‡¶ï‡ßá ‡¶∞‡ßá‡¶ü ‡¶ï‡¶®‡¶´‡¶ø‡¶ó ‡¶Ü‡¶®‡¶æ‡¶∞ ‡¶ú‡¶®‡ßç‡¶Ø ‡¶´‡¶æ‡¶Ç‡¶∂‡¶®
  const fetchFixedRate = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/rate-config");
      const data = await res.json();
      if (data) {
        return {
          ratePerKg: data.ratePerKg ?? 0,
          usdSurcharge: data.usdSurcharge ?? 0,
        };
      }
    } catch (err) {
      console.error("Fixed rate fetch error:", err);
    }
    return FIXED_RATE; // fallback
  };

  // LocalStorage ‡¶•‡ßá‡¶ï‡ßá ‡¶Ü‡¶ó‡ßá‡¶∞ ‡¶°‡ßá‡¶ü‡¶æ ‡¶≤‡ßã‡¶° ‡¶ï‡¶∞‡¶æ
  useEffect(() => {
    const savedData = localStorage.getItem("processedExcelData");
    if (savedData) {
      try {
        const parsedData = JSON.parse(savedData);
        setProcessedData(parsedData);
        if (onDataProcessed) {
          onDataProcessed(parsedData.items, parsedData.grandTotal);
        }
      } catch (error) {
        console.error("Error parsing saved data:", error);
        localStorage.removeItem("processedExcelData");
      }
    }
  }, [onDataProcessed]);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  // ‚úÖ ‡¶´‡¶æ‡¶á‡¶≤ ‡¶™‡ßç‡¶∞‡¶∏‡ßá‡¶∏ ‡¶ï‡¶∞‡¶æ
  const processExcelFile = (file, fixedRate) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();

      reader.onload = async (e) => {
        try {
