import React, { useState } from 'react';

const AdminPanel = () => {
  const [bills, setBills] = useState([
    { id: 1, customer: 'গ্রাহক A', amount: 2500, date: '2024-01-15', status: 'Pending' },
    { id: 2, customer: 'গ্রাহক B', amount: 3500, date: '2024-01-16', status: 'Approved' },
    { id: 3, customer: 'গ্রাহক C', amount: 1800, date: '2024-01-17', status: 'Rejected' }
  ]);

  const handleStatusChange = (id, status) => {
    setBills(prev => prev.map(bill => 
      bill.id === id ? { ...bill, status } : bill
    ));
  };

  return (
    <div className="card">
      <h3>বিল ম্যানেজমেন্ট</h3>
      <table>
        <thead>
          <tr>
            <th>বিল ID</th>
            <th>গ্রাহক</th>
            <th>পরিমাণ</th>
            <th>তারিখ</th>
            <th>স্ট্যাটাস</th>
            <th>অ্যাকশন</th>
          </tr>
        </thead>
        <tbody>
          {bills.map(bill => (
            <tr key={bill.id}>
              <td>{bill.id}</td>
              <td>{bill.customer}</td>
              <td>{bill.amount} টাকা</td>
              <td>{bill.date}</td>
              <td>{bill.status}</td>
              <td>
                <select 
                  value={bill.status} 
                  onChange={(e) => handleStatusChange(bill.id, e.target.value)}
                >
                  <option value="Pending">Pending</option>
                  <option value="Approved">Approved</option>
                  <option value="Rejected">Rejected</option>
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
